// App.js
import React, { useState } from "react";
import "./App.css";
import logo from "./logo2.png";
import LoginPage from "./LoginPage"; // Import the LoginPage component

function App() {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogin = () => {
    // Placeholder for login logic
    // Here you would check if the email exists and handle the password validation
    // For demonstration, let's assume the email does not exist
    setErrorMessage("Username/Email not found. Please create an account.");
  };

  return (
    <div className="App">
      {!showLoginForm ? (
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1>Welcome to Cindy Bliss</h1>
          <p>A journey through music, elegance, and inspiration.</p>
          <p onClick={() => setShowLoginForm(true)}>View Website</p>
        </header>
      ) : (
        <LoginPage onLogin={handleLogin} />
      )}
      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
}

export default App;
